import React from 'react'
import {useLocation} from "react-router-dom"

export default function Details(){
    const location = useLocation()
    const movie = location.state.movieData
    return(
        <div className = "movie">
             <div className = "Poster">
             <h3>{movie.Title} {movie.Year}</h3>
             <img src = {movie.Poster}/>
             </div>

             <div className = "Details">
                 <p style = {{width: "500px"}}>
                     <b>Release Date:</b> {movie.Released} &nbsp; <b>Runtime:</b> {movie.Runtime}
                     &nbsp; <b>Genre:</b> {movie.Genre} &nbsp; <b>Plot:</b> {movie.Plot}

                     &nbsp; <b>Director:</b> {movie.Director} &nbsp; <b>Writer:</b> {movie.Writer}
                     &nbsp; <b>Actors:</b> {movie.Actors} &nbsp; <b>Language:</b> {movie.Language}
                     &nbsp; <b>Country:</b> {movie.Country} &nbsp; <b>Awards:</b> {movie.Awards}
                     &nbsp; <b>Country:</b> {movie.Country} &nbsp; <b>Awards:</b> {movie.Awards}
                 
                 </p>
                 
             </div>
             
                
        </div>
    )
}
