// this is the reducer file

const UpdateReducer=(state= {"movie":"spiderman"}, action)=>{
    switch(action.type){
        case 'UPDATE':
            return {...state, "movie": action.payLoad}
        default:
            return state
    }

}

export default UpdateReducer
