import React from 'react'
import {GiMagnifyingGlass} from "react-icons/gi"
import 'bootstrap/dist/css/bootstrap.css';
import {DropdownButton, Dropdown} from 'react-bootstrap'
import RedirectButton from './Redirect';
import { useSelector } from 'react-redux';
import { useState } from 'react';


export default function MoviesList(){
    const [selected, setSelected] = useState(false)
    var years = [1999,2000,2001,2002,2018]
    var feedBack = ""
    if(!selected){
     feedBack = "You have not selected a movie yet" 
    }else if(Response ==="False"){
     feedBack = "Movie is not found"
    } 

    handleSelect =(eventkey)=>{
        console.log(eventkey, " heo")
        this.setState({yearSelected: eventkey})
        
    }
    myTextChangeHandler =(event)=>{
        this.setState({movieSearched : event.target.value})
    }
    fetchMovie=()=>{
        this.setState({selected:true})
        fetch("http://www.omdbapi.com/?apikey=3e3f65ea&t=" + this.state.movieSearched  +"&y" + this.state.yearSelected)
        .then(response => response.json())
        .then(data => this.setState({data:data}))
    }      
        return(
            <div  className = "ListPage">
                <h1>Search A Movie</h1>
                <div className = "ListBox">                
                <div className = "filters">
                    <div className ="searchBar">
                        <input className = "searchField"
                            type='text'
                            placeholder = "Search by movie name"
                            onChange = {this.myTextChangeHandler}
                        />
                        <GiMagnifyingGlass size = "20px" color = "grey"/>
                    </div>
                    <div className= "dropDown" >
                        <DropdownButton variant = "success" id = "dropdown-basic-button" title= "year" onSelect = {this.handleSelect}>
                            {years.map( year => <Dropdown.Item eventKey = {year}> {year} </Dropdown.Item>)}
                        </DropdownButton>
                    </div>    
                    <button className = "btnn" onClick = {this.fetchMovie}>Submit</button>
                </div>              
                <hr/>
                <div>
                     {feedBack} 
                     <p>Title: {this.state.data.Title}</p>
                     <p>Year:{this.state.data.Year}</p>
                     <RedirectButton movieData = {this.state.data}/>
                </div>
            </div>
            </div>
            
            
        )
}



