import React from 'react'
import SingleMovie from './singleMovie';
import {GiMagnifyingGlass} from "react-icons/gi"
import 'bootstrap/dist/css/bootstrap.css';
import {DropdownButton, Dropdown} from 'react-bootstrap'
export default class MoviesList extends React.Component{
    constructor(){
        super();
        // var years = [0]
        // for(let i = 1999; i++; i < 2021){
        //     years.push(i)
        // }
        this.state = {yearSearched:0, movieSearched: ""}
    }

    handleSelect =(eventkey, event)=>{
        event.persist();
        // alert(event)
        alert(eventkey)
    }
    render(){
       var years = [1999,2000,2001,2002]
        var num = 0
        return(
            <div className = "ListPage">
                <div className = "filters">
                    <div className ="searchBar">
                        <input className = "searchField"
                            type='text'
                            placeholder = "Search by movie name"
                            onChange = {e =>this.myChangeHandler}
                        />
                        <GiMagnifyingGlass size = "20px" color = "grey"/>
                    </div>
                    <div className= "dropDown" >
                        <DropdownButton variant = "success" id = "dropdown-basic-button" title= "year" onSelect = {this.handleSelect}>
                            {years.map( year => <Dropdown.Item> {year} </Dropdown.Item>)}
                        </DropdownButton>
                    </div>                   
                    
                </div>
                

            </div>
            
        )
    }
}
